Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (630,3,'（１）を凍ったまま５ｍｍ角に切ります。クッキーはあらくくだきます。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (630,4,'（２）、（３）を合わせ、器に盛り、ミントをのせます。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (631,1,'ゆり根は１枚ずつはがします。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (631,2,'ねぎはみじん切りにし、Ａと合わせます。');
