Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (632,4,'（３）を皿に盛り、パセリのみじん切りをかけます。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (633,1,'切り干しだいこんは、さっと洗って水に５分つけます。');
