Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (601,1,'紫たまねぎは薄切りにします。５分ほど水にさらして、水気をきります。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (601,2,'アボカドは約１ｃｍ角に切り、Ａをまぶします。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (601,3,'ミックスナッツはあらくきざみます。Ｂは合わせます。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (601,4,'器にたまねぎとアボカドを盛り、ナッツを散らして、Ｂをかけます。');
