Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (261,5,'（３）を盛り、あんをかけます。おろししょうが、にんじんを添えます。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (262,1,'タアサイは５cm長さに切ります。Ａはみじん切りにします。');
Insert into SANTE.STEPS (RECIPE_ID,STEP,DESCRIPTION) values (262,2,'Ｂは合わせます。');
