  SELECT R.RECIPE_ID AS RECIPE_ID, R.QUANTITY AS QUANTITY, M.MATERIAL_NAME AS MATERIAL_NAME, M.GRAM_PER_QUANTITY AS GRAM_PER_QUANTITY, M.PREFIX AS PREFIX, M.POSTFIX AS POSTFIX, M.NUTRIENT_ID AS NUTRIENT_ID  FROM RECIPE_MATERIALS R INNER JOIN MATERIALS M ON (R.MATERIAL_NAME = M.MATERIAL_NAME AND R.PREFIX = M.PREFIX AND R.POSTFIX = M.POSTFIX);
